import { useState } from 'react'

const BasicForm = (props) => {
  const [enteredName, setEnteredName] = useState('')
  const [enteredNameTouched, setEnteredNameTouched] = useState()


  const enteredNameIsValid = enteredName.trim() !== '';
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched


  let formIsValid = false

  //form input validity
  if(enteredNameIsValid){
    formIsValid = true
  }

  const nameInputChangeHandler = event => {
    setEnteredName(event.target.value)
  }

  const nameInputBlurHandler = event => {
    setEnteredNameTouched(true)
  }

  const formSubmissionHandler = event => {
    event.preventDefault()

    setEnteredNameTouched(true)
  }

  if(!enteredNameIsValid){
    return;
  }

  setEnteredName('')
  setEnteredNameTouched(false)


  const nameIputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control';


  return (
    <form onSubmit={formSubmissionHandler}>
      <div className='control-group'>
        <div className={nameIputClasses}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name'
          onChange= {nameInputChangeHandler}
          onBlur = {nameInputBlurHandler}
          value={enteredName} />
          {nameInputIsInvalid && <p className='error-text'></p>}

        </div>


        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name'
           onChange= {nameInputChangeHandler}
           onBlur = {nameInputBlurHandler}
           value={enteredName} />

        {nameInputIsInvalid && <p className='error-text'></p>}
        </div>
      </div>


      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
