import useInput from '../hooks/use-input';

const BasicForm = (props) => {
  // definition functions to call state

const {
  value: enteredFirstName,
  isValid: enteredFirstNameIsValid,
  hasError: firstNameHasError,
  valueChangeHandler: firstNameChangeHandler,
  InputBlurHandler: firstNameBlurHandler,
  reset: resetFirstName,

} = useInput (value => value.trim() !== '')

const {
  value: enteredLastName,
  isValid: enteredLastNameIsValid,
  hasError: lastNameHasError,
  valueChangeHandler: lastNameChangeHandler, 
  InputBlurHandler: lastNameBlurHandler,
  reset: resetLastName,
} = useInput (value => value.trim() != '')

const {
  value: enteredEmail,
  isValid: enteredEmailIsValid,
  hasError: emailHasError,
  valueChangeHandler: emailChangeHandler, 
  InputBlurHandler: emailBlurHandler,
  reset: resetEmail,
} = useInput (value => value.includes('@'))


  //set validdity
  let formIsValid = false

  //form input validity
  if(enteredFirstNameIsValid && enteredLastNameIsValid && enteredEmailIsValid){
    formIsValid = true
  }


  //Form submission
  const formSubmissionHandler = event => {
    event.preventDefault()

    // if(!enteredNameIsValid && enteredEmailIsValid){
    //   return;
    // }


  resetFirstName()

resetLastName('')

resetEmail('')

  }

// input class control
  const firstNameIputClasses = firstNameHasError ? 'form-control invalid' : 'form-control';
  const lastNameIputClasses = lastNameHasError? 'form-control invalid' : 'form-control';
  const emailIputClasses = emailHasError ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className='control-group'>
        <div className={firstNameIputClasses}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name'
          onChange= {firstNameChangeHandler}
          onBlur = {firstNameBlurHandler}
          value={enteredFirstName} />
          {firstNameHasError && <p className='error-text'>Enter a valid First Name</p>}

        </div>


        <div className={lastNameIputClasses}>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name'
           onChange= {lastNameChangeHandler}
           onBlur = {lastNameBlurHandler}
           value={enteredLastName} />

        {lastNameHasError && <p className='error-text'> Enter a valid Last name</p>}
        </div>
      </div>


      <div className={emailIputClasses}>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name'
         onChange= {emailChangeHandler}
         onBlur = {emailBlurHandler}
         value={enteredEmail} />

      {emailHasError && <p className='error-text'> Enter a valid Email</p>}

      </div>
      <div className='form-actions'>
      <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
